mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num,writer,title,content,viewCount,regdate FROM board_cafe
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE board_cafe
SET viewCount=viewCount+1
WHERE num=#{num}

UPDATE board_cafe
SET title=#{title}, content=#{content}
WHERE num=#{num}
